import ChangeRadius from "./RadiusChange"
import { Data } from "./Data"
import React from "react"


class SearchForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
      document.addEventListener('click', (e) => {
        const toggle = document.querySelector('.search__toggle')
        const input = document.querySelector('.search__input')
        const clickedElement = e.target

        if (clickedElement == toggle) {
          input.value = ''
          return;
        }

        const isSearchField = clickedElement.closest('.search__field')    

        if (!isSearchField) {
          toggle.checked = false
          input.value = ''
        }
      })
    }


    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
      window.location.href="?location="+this.state.value
      event.preventDefault();
    }

    render() {
      return (
        // <form className="loop" onSubmit={this.handleSubmit}>
        //   <input className="inputSerach" type="text" value={this.state.value} onChange={this.handleChange} />
        //   <input className="buttonSearch" type="submit" value="" />
        // </form>
        <form class="search loop" onSubmit={this.handleSubmit}>
          <input type="checkbox" id="toggleSearch" class="search__toggle" hidden />
          <div class="search__field">
            <label for="toggleSearch" class="search__label">
              <span class="search__close"></span>
            </label>
            <input type="text" class="search__input" value={this.state.value} onChange={this.handleChange}  placeholder="What are you looking for?" />
            <label for="toggleSearch" class="search__label">
              <div class="search__button">
                <div class="search__icon search__button--toggle"></div>
              </div>
              <button class="search__button search__button--submit">
                <div class="search__icon"></div>
              </button>
            </label>
          </div>
        </form>
      );
    }
  }

export default SearchForm
