import React, { useEffect } from 'react';
import $ from "jquery"
function ChatBot() {

    const loader = (element) =>{
        element.textContent = '';
        let loadInterval;

        loadInterval = setInterval(() => {
          element.textContent+='.';

          if(element.textContent === '....'){
            element.textContent = '';
          }
        },300)
      }


      const typeText = (element,text) =>{
        let index = 0;

        let interval = setInterval(() => {
          if(index < text.length){
            element.innerHTML += text.charAt(index);
            document.querySelector('#chat_container').scrollTop = document.querySelector('#chat_container').scrollHeight;
            index++;
          }else{
            clearInterval(interval);
          }
        },10)
      }

      const generateUniqueId = () =>{
        const timestamp = Date.now();
        const randomNumber = Math.random();
        const hexadecimalString = randomNumber.toString(16);

        return `id-${timestamp}-${hexadecimalString}`;
      }

      const chatStripe = (isAi,value,uniqueId) =>{
        return(
          `
            <div class="wraooer ${isAi && 'ai'}">
              <div class="chat">
                <div class = "profile">
                  <img
                    src="${isAi ? "bot.svg" : "user.svg"}"
                    alt="${isAi? 'bot' : 'user'}"
                  />
                </div>
                <div class = "message" id=${uniqueId}>${value}</div>
              </div>
            </div>
          `
        )
      }


    const handleSubmit = async (e) =>{
        const chatContainer = document.querySelector('#chat_container');
        e.preventDefault();
        const quest = document.getElementById("prompt").value;

        chatContainer.innerHTML+=chatStripe(false,quest);
        document.getElementById("prompt").value="";


        const uniqueId = generateUniqueId();
        chatContainer.innerHTML+=chatStripe(true," ",uniqueId);

        chatContainer.scrollTop = chatContainer.scrollHeight;

        const messageDiv = document.getElementById(uniqueId);
        loader(messageDiv)

        const respons = await fetch('http://localhost:5001/openAi',{
          method:'POST',
          headers:{
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            prompt: quest
          })
        })
        messageDiv.innerHTML = "";

        if(respons.ok){
          const data = await respons.json();
          const parsedData = data.bot.trim();

          typeText(messageDiv,parsedData);
        }else{
          const err = await respons.text();

          messageDiv.innerHTML = "Something went wrong";
          alert(err);
        }
      }

    const openChat = () =>{
        if($(".chat-app").hasClass("active")){
            $(".chat-app").removeClass("active");
        }else{
            $(".chat-app").addClass("active");
        }
    }

    useEffect(()=>{
        document.getElementById("prompt").addEventListener('keyup', (e)=>{
            if(e.keyCode ===13){
              handleSubmit(e);
            }
          })
    }) 

    return (
            <div id="app" className='chat-bot'>
                <div className='chat-app'>
                    <div id="chat_container"></div>
                    <textarea name="prompt" id="prompt" rows="1" cols="1" placeholder="Ask...."></textarea>
                    <button type="submit" onClick={handleSubmit}><img src="send.svg"></img></button>
                </div>
                <button id='message-button' onClick={openChat}>
                    <i class="fa fa-commenting-o"></i>
                  </button>
            </div>
    );
}

export default ChatBot;
