import React, { useEffect } from 'react';
import $ from "jquery"

// import bot from 'bot.svg';
// import user from './assets/user.svg';

function ChatBot() {

    const handleClick = (e) => {
        if($(".chat-app").hasClass("active")){
            $(".chat-app").removeClass("active");
        }else{
            $(".chat-app").addClass("active");
        }
      }
      const handleSubmit = async (e) =>{
        e.preventDefault();
        //const data = new FormData(form);

        chatContainer.innerHTML+=chatStripe(false,$("#prompt").value);
        $("#prompt").value="";
        //form.reset();

        const uniqueId = generateUniqueId();
        chatContainer.innerHTML+=chatStripe(true," ",uniqueId);

        chatContainer.scrollTop = chatContainer.scrollHeight;

        const messageDiv = document.getElementById(uniqueId);
        loader(messageDiv)

        const respons = await fetch('http://localhost:5001/openAi',{
          method:'POST',
          headers:{
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            prompt: $("#prompt").value
          })
        })

        clearInterval(loadInterval);
        messageDiv.innerHTML = "";

        if(respons.ok){
          const data = await respons.json();
          const parsedData = data.bot.trim();

          typeText(messageDiv,parsedData);
        }else{
          const err = await respons.text();

          messageDiv.innerHTML = "Something went wrong";
          alert(err);
        }
      }
      //const form = document.getEle('form');
      const chatContainer = document.querySelector('#chat_container');

      let loadInterval;

      function loader(element){
        element.textContent = '';

        loadInterval = setInterval(() => {
          element.textContent+='.';

          if(element.textContent === '....'){
            element.textContent = '';
          }
        },300)
      }


      function typeText(element,text){
        let index = 0;

        let interval = setInterval(() => {
          if(index < text.length){
            element.innerHTML += text.charAt(index);
            index++;
          }else{
            clearInterval(interval);
          }
        },20)
      }

      function generateUniqueId(){
        const timestamp = Date.now();
        const randomNumber = Math.random();
        const hexadecimalString = randomNumber.toString(16);

        return `id-${timestamp}-${hexadecimalString}`;
      }

      function chatStripe(isAi,value,uniqueId){
        return(
          `
            <div class="wraooer ${isAi && 'ai'}">
              <div class="chat">
                <div class = "profile">
                  <img
                    src="${isAi ? "bot.svg" : "user.svg"}"
                    alt="${isAi? 'bot' : 'user'}"
                  />
                </div>
                <div class = "message" id=${uniqueId}>${value}</div>
              </div>
            </div>
          `
        )
      }

      useEffect(() => {
        var element = document.getElementById("submitQues")

        element.addEventListener('submit',handleSubmit);
        element.addEventListener('keyup', (e)=>{
          if(e.keyCode ===13){
            handleSubmit(e);
          }
        })
      })





    return (
        <div className='chat-bot'>
            <div className='chat-app'>

                <div id="chat_container"></div>
                <div className='form'>
                    <textarea name="prompt" rows="1" cols="1" placeholder="Ask...." id="prompt"></textarea>
                    <button type="submit" id="submitQues" onClick={handleSubmit}>
                        <img src='send.svg'></img>
                    </button>
                </div>
            </div>
            <button id='message-button' onClick={handleClick}><img src='message2.png'></img></button>
        </div>
    );
}

export default ChatBot;
