import './Map.css';
import { MapContainer, TileLayer,Marker,Circle} from 'react-leaflet'
import "leaflet/dist/images/marker-shadow.png";
import 'leaflet/dist/leaflet.css';
import 'leaflet-defaulticon-compatibility/dist/leaflet-defaulticon-compatibility.webpack.css';
import React from 'react';
import "leaflet/dist/leaflet.css";
import "leaflet-geosearch/dist/geosearch.css";
import { Data } from '../Data';
import { ICONRED,theRadius } from '../Variable';
import ChatBot from '../ChatBot';
import MarkerRadius from '../MarkerRadius';

export class MyMap extends React.Component {
  constructor(props){
      super(props);
      this.state = {
        lan: props.lan,
        lon:props.lon,
        cityName:props.cityName,
        items:[],
        kilometers:1
      }
  }
  // const [kilometers,setKilometers] = useState(1);
  changeKilometers (event){
    this.setState({
      kilometers : event.target.value,
    })
  };

  componentDidMount(){
    fetch("https://nominatim.openstreetmap.org/search?format=json&limit=3&q=" + this.state.cityName)
    .then(res => res.json())
    .then(
      (result) => {

        this.setState({
          isLoaded: true,
          items: result
        });

    },
      (error) => {
        this.setState({
          isLoaded: true,
          error
        });
      }
    )

    const slideValue = document.querySelector("span");
    const inputSlider = document.querySelector("input");
    inputSlider.oninput = (()=>{
      let value = inputSlider.value;
      slideValue.textContent = value;
      slideValue.style.left = (value/2) + "%";
      slideValue.classList.add("show");

    });
    inputSlider.onblur = (()=>{
      slideValue.classList.remove("show");
    });
  }

  render() {

      if (this.state.items.length>0){
        return (
          <div>
          <MapContainer center={[this.state.items[0].lat,this.state.items[0].lon]} zoom={14} style={{height: "100vh"}}>
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://api.maptiler.com/maps/voyager/256/{z}/{x}/{y}.png?key=tbxxnHOYRKXeGzScTE2D" />
              {/* <Marker icon={ICONRED} position={[this.state.items[0].lat,this.state.items[0].lon]}></Marker> */}
            {/* <Data items={this.state.items} city={this.state.cityName} radius={theRadius} ></Data> */}
            <MarkerRadius center={[this.state.items[0].lat,this.state.items[0].lon]} radius= {this.state.kilometers}></MarkerRadius>
            </MapContainer>
            <div className="range-operations">
                {/* <span>Range: {this.state.kilometers}</span>
                <input
                    type='range'
                    min={1}
                    onChange={(event) => this.setState({ kilometers: event.target.value})}
                    max={10000}
                    step={1}
                    orient="vertical" 
                    value={this.state.kilometers}
                    className='custom-slider'>
                </input> */}
                <div class="range">
                <div class="sliderValue">
                  <span>100</span>
                </div>
                <div class="field">
                  <div class="value left">0</div>
                  <input type="range" onChange={(event) => this.setState({ kilometers: event.target.value})} min="0" max="20000" value={this.state.kilometers} steps="1"/>
                  <div class="value right">20000</div>
                </div>
              </div>
            </div>
          </div>

        )
      }else{
        return "";
      }


  }
}
