import React from 'react';
import $ from "jquery"

function ChatBot() {

    const handleClick = (e) => {
        if($(".chat-app").hasClass("active")){
            $(".chat-app").removeClass("active");
        }else{
            $(".chat-app").addClass("active");
        }
      }
      const handleSubmit = async (e) =>{
        e.preventDefault();
        const data = new FormData(form);

        chatContainer.innerHTML+=chatStripe(false,data.get('prompt'));
        form.reset();

        const uniqueId = generateUniqueId();
        chatContainer.innerHTML+=chatStripe(true," ",uniqueId);

        chatContainer.scrollTop = chatContainer.scrollHeight;

        const messageDiv = document.getElementById(uniqueId);
        loader(messageDiv)

        const respons = await fetch('http://localhost:5001/openAi',{
          method:'POST',
          headers:{
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            prompt: data.get('prompt')
          })
        })

        clearInterval(loadInterval);
        messageDiv.innerHTML = "";

        if(respons.ok){
          const data = await respons.json();
          const parsedData = data.bot.trim();

          typeText(messageDiv,parsedData);
        }else{
          const err = await respons.text();

          messageDiv.innerHTML = "Something went wrong";
          alert(err);
        }
      }

    return (
        <div className='chat-bot'>
            <div className='chat-app'>

                <div id="chat_container"></div>
                <div className='form'>
                    <textarea name="prompt" rows="1" cols="1" placeholder="Ask...."></textarea>
                    <button type="submit" onClick={handleSubmit}>
                        <img src='send.svg'></img>
                    </button>
                </div>
            </div>
            <button id='message-button' onClick={handleClick}><img src='message2.png'></img></button>
        </div>
    );
}

export default ChatBot;
