import React from 'react';
import $ from "jquery"

function ChatBot() {

    const handleClick = (e) => {
        // e.preventDefault();
        // console.log('The link was clicked.');
        if($(".chat-app").hasClass("active")){
            $(".chat-app").removeClass("active");
        }else{
            $(".chat-app").addClass("active");
        }
      }

    return (
        <div className='chat-bot'>
            <div className='chat-app'>

                <div id="chat_container"></div>
                <div className='form'>
                    <textarea name="prompt" rows="1" cols="1" placeholder="Ask...."></textarea>
                    <button type="submit">
                        <img src='send.svg'></img>
                    </button>
                </div>
            </div>
            <button id='message-button' onClick={handleClick}><img src='message2.png'></img></button>
        </div>
    );
}

export default ChatBot;
