import { useEffect, useState } from "react";
import CustomGeoJson from "./geoJson";
export default function MapPoly(props){
    const [dataJson,setDataJson] = useState();
    const [isSet,setIsSet] = useState(false);
    const [style,setStyle] = useState();
    // function style(features) {
    //     return {

    //       fillColor: features.properties.fill,
    //       weight: 1,
    //       color: "#000",
    //       weight:0.5,
    //       dashArray: '0',
    //       opacity: 0.4,
    //       fillOpacity: 0.4
    //     };
    // }


      useEffect(()=>{

        const fetchData = (()=>{
            let data = import('./json/world_'+props.finalNumber+'.json');

            data.then(d=>{
                setDataJson(d.features);
                setIsSet(true);
                //var sty = {fillColor:d.features.fill,weight:1,color:'#000',weight:0.5,dashArray:'0',opacity:0.4,fillOpacity:0.4};
                let style = () => { fillColor: 'green' };

                setStyle(style);
            })
        })

        fetchData();

      },[props.finalNumber])


        return(
            <>
            {isSet && <CustomGeoJson key={props.finalNumber} data={dataJson} style={style}/>}

            </>
        )
}
