import MapPoly from "./mapPoly";

export default function AddPolygon(props){
    const [finalNumber,setFinalNumber] = useState();
    const [yearForGeoJson,setYearForGeoJson]= useState([-1,-1000,-200,-2000,-323,-500,1000,117,1279,1492,1530,1650,1715,1783,1815,1830,1850,1861,1872,1880,1914,1920,1938,1939,1940,1941,1942,1943,1944,1945,1946,1994,2020,400,600,800])
    const [yearForPoly,setYearForPoly] = useState();

    useEffect(() => {

    //var yearForGeoJson = [-1,-1000,-200,-2000,-323,-500,1000,117,1279,1492,1530,1650,1715,1783,1815,1830,1850,1861,1872,1880,1914,1920,1938,1939,1940,1941,1942,1943,1944,1945,1946,1994,2020,400,600,800]
    //var yearForPoly = props.yearMarker;
    setYearForPoly(props.yearMarker);
    if(yearForPoly<=0){
          if(yearForPoly<0 && yearForPoly>-200){
            setFinalNumber("BC-"+1+"-1-1");
          }else if(yearForPoly<-200 && yearForPoly>-323){
            setFinalNumber("BC-"+200+"-1-1");
          }else if(yearForPoly<-323 && yearForPoly>-500){
            setFinalNumber("BC-"+323+"-1-1");
          }else if(yearForPoly<-500 && yearForPoly>-1000){
            setFinalNumber("BC-"+500+"-1-1");
          }else if(yearForPoly<-1000 && yearForPoly > -2000){
            setFinalNumber("BC-"+-1000+"-1-1");
          }else if(yearForPoly<-2000 ){
            setFinalNumber("BC-"+2000+"-1-1");
          }
    }else{
        let arrayYearJson=[];
        let place;
        yearForGeoJson.forEach(function(array) {
            arrayYearJson.push(array);
        })
        arrayYearJson.push(yearForPoly);
        arrayYearJson.sort(function(a, b){return a-b});
        arrayYearJson.forEach(function(array,index){
            if(arrayYearJson[index]==yearForPoly){
                place=index;
            }
        })
        var finalnumber=arrayYearJson[place-1];
        var yearFin;
        if(finalnumber<0){
            finalnumber= finalnumber*(-1);
            yearFin = "BC-"+finalnumber+"-1-1"
        }else{
            yearFin = "AD-"+finalnumber+"-1-1";
        }
        setFinalNumber(yearFin);

    }

    }, [finalNumber]);


    return(
        <>
            <MapPoly finalNumber={finalNumber}></MapPoly>
        </>
    )


}
