import React from 'react';
import ReactDOM from 'react-dom/client';
import {Marker,Popup} from 'react-leaflet'
import { icon } from '../Variable';



export class CustomMarker extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            renderedThings:[],
            itemsRendered:0,
            hidden:"hidden"
        }
    }
    dateFormater(date){
      const words = date.split('/');
      var mon=words[0]
      var day=words[1]
      var year=words[2]

      if(mon = 1){
        mon = "Jan"
      }else if (mon = '2'){
        mon = "Feb"
      }else if (mon = '3'){
        mon = "Mar"
      }else if (mon = '4'){
        mon = "Apr"
      }else if (mon = '5'){
        mon = "May"
      }else if (mon = '6'){
        mon = "Jun"
      }else if (mon = '7'){
        mon = "Jul"
      }else if (mon = '8'){
        mon = "Avg"
      }else if (mon = '9'){
        mon = "Sep"
      }else if (mon = '10'){
        mon = "Oct"
      }else if (mon = '11'){
        mon = "Nov"
      }else if (mon = '12'){
        mon="Dec"
      }
      return mon+" "+day + " " +year
    }

    // componentDidMount() {


    //   data.map((element, i) => {
    //       this.setState({renderedThings:element})
    //   })
    // }

    componentDidMount() {
      //const data = [this.props.things] 
      for (let i in this.props.things) {
        setTimeout(() => {
          this.setState({
            renderedThings: this.props.things[i],
          });
        }, 2000 * i);
      }
    }


    show(){
      this.setState({hidden : ""});
    }


    render() {
      return (
        //this.state.renderedThings.map((element, i) => {
          //return(
            <Marker className={this.state.hidden} key={this.state.renderedThings[0].key} icon={icon(this.state.renderedThings[0].data.img_name)} position={[this.state.renderedThings[0].data.lat,this.state.renderedThings[0].data.long_marker]}>
              <Popup>
                  <a className='description-name' href={this.state.renderedThings[0].data.wiki}>{this.state.renderedThings[0].data.marker_name}</a><br></br>
                  {this.state.renderedThings[0].data.text_wrap}<br></br>
                  <span className='description-date'>Date:</span> {this.dateFormater(this.state.renderedThings[0].data.date)} {this.state.renderedThings[0].data.bc_ad}
              </Popup>
            </Marker>
          //)
        //})
      )
    }
}
